             return false;
        }
    }

    const  validationPhone = (value, message, message1, limit=11) =>{
        if(value != false){
            if(value.length >= limit){
                if(/^\+?([0-9]{2})\)?[-. ]?([0-9]{4})[-. ]?([0-9]{4})$/.test(value)){
                     errorMsg['phone'] = '';
                }else{
                     errorMsg['phone'] = 'Phone Number is invalid';
                     return false;
