                 errorMsg['phone'] = message1;
                 return false;
            }
              
        }else{
            
             errorMsg['phone'] = message;
             return false;
        }
    }

    const  validationPassword = (value, message, message1, message2, limit=4) =>{
        if(value != false){
            let difference = limit - value.length;
                if (value.length<limit) {
                    errorMsg['password'] = 'Must be '+limit+' characters! '+ difference + ' characters left' ;
                } else {
                    if(value.length<4 && value.length> 8){
                        errorMsg['password'] = message1 ;
                    }else{
                         errorMsg['password'] = message2 ;
                    }
                }
              
        }else{
            
             errorMsg['phone'] = message;
             return false;
        }
    }

const Validation = () =>{
    return { validationText, validationEmail, validationCheckbox, validationPhone, validationPassword, errorMsg}
}

