import { computed, reactive, readonly } from "vue"
import SiteOverlayStore from  "../../../store/SiteOverlayStore.js"
const { SiteOverlayShow, SiteOverlayHide } = SiteOverlayStore();

const state = reactive({
    PdtCategorySlide:false,
    PdtFilterSlide:false,
})

const getters = {
    PdtCategorySlide : () => {
        return computed(() => { return state.PdtCategorySlide });
    },
    PdtFilterSlide : () => {
        return computed(() => { return state.PdtFilterSlide });
    },
}

const mutations = {

}

const actions = {
    PdtFilterSlideBtn : (value) => {
        if(value == 'show'){
            state.PdtCategorySlide = true;
            SiteOverlayShow(value);
        }else{
            state.PdtCategorySlide = false;
            SiteOverlayHide(value);
        }
    },
}

export default () => {
    return {
        state:readonly(state),
        ...getters,
        ...mutations,
        ...actions,
    }
}
