import { computed, reactive, readonly } from "vue"

const state = reactive({
    SiteOverlay:false,
})

const getters = {
    SiteOverlay : () => {
        return computed(() => { return state.SiteOverlay });
    },
}

const mutations = {
    SiteOverlayShow : () => {
        state.SiteOverlay = true;
    },
    SiteOverlayHide : () => {
        state.SiteOverlay = false;
    },
}

const actions = {
    
}

export default () => {
    return {
        state:readonly(state),
        ...getters,
        ...mutations,
        ...actions,
    }
}
